<?php

namespace App\Http\Controllers;

use App\Models\Menu;
use App\Http\Requests\StoremenuRequest;
use App\Http\Requests\UpdatemenuRequest;
use Illuminate\Support\ViewErrorBag;

class MenuController extends Controller
{
    /**
     * Display a listing of the resource.
     */
    public function index()
    {
        $menus = Menu::all();

        return view('manage_sell_menu.index', compact("menus"));
    }

    /**
     * Show the form for creating a new resource.
     */
    public function create()
    {
        $menus = 'Tambah Menu';
        return view('manage_sell_menu.create', compact('menus'));
    }

    /**
     * Store a newly created resource in storage.
     */
    public function store(StoremenuRequest $request)
    {
        $validatedData = $request->validate([
            'kode_tenant'=>'required|string',
            'nama_menu'=> 'required|string|max:100',
            'deskripsi'=> 'required|string',
            'harga'=> 'required|integer',
            'stok'=> 'required|integer|min:1',
            'status'=> 'required|in:tersedia,tidak_tersedia',
            'image'=> 'nullable|image|mimes:jpeg,png,jpg,gif|max:2048'
        ]);

        // Handle image upload as Base64
        $base64Image = "data:image/jpeg;base64,";

        if ($request->hasFile('image')) {
            $image = $request->file('image');
            $imageContent = file_get_contents($image->getRealPath());
            $base64Image = "data:image/png;base64,".base64_encode($imageContent);
        }

        // // Save the data to the database
        $menu = Menu::create([
            'kode_tenant' => $validatedData['kode_tenant'],
            'nama_menu' => $validatedData['nama_menu'],
            'deskripsi' => $validatedData['deskripsi'],
            'harga' => $validatedData['harga'],
            'stok' => $validatedData['stok'],
            'status' => $validatedData['status'],
            // 'penjual_id' => $validatedData['penjual_id'],
            'penjual_id' => 1,
            'image' => $base64Image, // Save Base64 string to the database
        ]);

        return redirect()->route('manage_menu_sell.index')->with('sucess','Menu Berhasil Ditambahkan');
    }

    /**
     * Display the specified resource.
     */
    public function show(Menu $menu)
    {
        $menus = 'Detail Menu';
        return view('index.show', compact('menus'));
    }

    /**
     * Show the form for editing the specified resource.
     */
    public function edit(Menu $menu)
    {
        $menus = 'Edit Menu';
        return view('manage_sell_menu.edit', compact('menu'));
    }

    /**
     * Update the specified resource in storage.
     */
    public function update(UpdatemenuRequest $request, Menu $menu)
    {
        $validatedData = $request->validate([
            'kode_tenant'=>'required|string',
            'nama_menu'=> 'required|string|max:100',
            'deskripsi'=> 'required|string',
            'harga'=> 'required|integer',
            'stok'=> 'required|integer|min:1',
            'status'=> 'required|in:tersedia,tidak_tersedia',
        ]);
                // Handle image upload as Base64
                $base64Image = $menu->image;
                if ($request->hasFile('image')) {
                    $image = $request->file('image');
                    $imageContent = file_get_contents($image->getRealPath());
                    $base64Image = "data:image/png;base64,".base64_encode($imageContent);
                }

                // // Save the data to the database
                $menu->update([
                    'kode_tenant' => $validatedData['kode_tenant'],
                    'nama_menu' => $validatedData['nama_menu'],
                    'deskripsi' => $validatedData['deskripsi'],
                    'harga' => $validatedData['harga'],
                    'stok' => $validatedData['stok'],
                    'status' => $validatedData['status'],
                    // 'penjual_id' => $validatedData['penjual_id'],
                    'penjual_id' => 1,
                    'image' => $base64Image, // Save Base64 string to the database
                ]);

        return redirect()->route('manage_menu_sell.index')->with('success','Menu Berhasil Diperbarui');
    }

    /**
     * Remove the specified resource from storage.
     */
    public function destroy(Menu $menu)
    {
        $menu->delete();
        return redirect()->route('manage_menu_sell.index')->with('success','Menu Berhasil Dihapus');
    }
}
